// custom.d.ts or types/assets.d.ts
declare module '*.svg' {
  const content: string;  // This should be string, not ReactComponent
  export default content;
}

declare module '*.png' {
  const value: string;
  export default value;
}

declare module '*.jpg' {
  const value: string;
  export default value;
}

declare module '*.jpeg' {
  const value: string;
  export default value;
}

declare module '*.gif' {
  const value: string;
  export default value;
}